package test.pkg;



public class Pattern {

	

public void testPattern()
{
	for(int i=5;i<=5;i--)
	{
		for(int j=0;j<=i;j++)
		{
			System.out.print("*");
			
		}
		System.out.println();
	}
}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Pattern p=new Pattern();
		p.testPattern();
		
       
	}

}
